mapper.insert:
INSERT INTO BOARD (
	BOARD_NUM
	, BOARD_TITLE
	, BOARD_CONTENT
	, BOARD_WRITER
	, BOARD_MENU_CODE

	, IS_IMPORTANT

) VALUES (
	(SELECT 'BOARD_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(BOARD_NUM, 7))), 0) + 1, 3, '0')
    FROM BOARD)
	, #{boardTitle}
	, #{boardContent}
	, 20230517
	, 'BOARD_MENU_001'

	, #{isImportant}

)

mapper.delete:
DELETE BOARD
WHERE BOARD_NUM = #{boardNum}
AND BOARD_MENU_CODE = 'BOARD_MENU_001'

mapper.update:
UPDATE BOARD
SET BOARD_TITLE = #{boardTitle}
	, BOARD_CONTENT = #{boardContent}
	, IS_IMPORTANT = #{isImportant}
WHERE BOARD_NUM = #{boardNum}
AND BOARD_MENU_CODE = #{boardMenuVO.boardMenuCode}
