mapper.insert:
INSERT INTO BOARD (
	BOARD_NUM
	, BOARD_TITLE
	, BOARD_CONTENT
	, BOARD_WRITER
	, BOARD_MENU_CODE

	, IS_IMPORTANT

) VALUES (
	#{boardNum}
	, #{boardTitle}
	, #{boardContent}
	, 20230517
	, 'BOARD_MENU_001'

	, #{isImportant}

)

INSERT INTO BOARD_FILE (
	FILE_NUM
	, ORIGIN_FILE_NAME
	, ATTACHED_FILE_NAME
	, FILE_SIZE
	, BOARD_NUM
) 

	SELECT
		(SELECT 'FILE_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(FILE_NUM, 6))), 0) + 1 + #{i}, 3, '0')
			FROM BOARD_FILE)
		, #{boardFile.originFileName}
		, #{boardFile.attachedFileName}
		, #{boardFile.fileSize}
		, #{boardFile.boardNum}
	FROM DUAL

mapper.delete:
DELETE BOARD
WHERE BOARD_NUM = #{boardNum}
AND BOARD_MENU_CODE = 'BOARD_MENU_001'

mapper.update:
UPDATE BOARD
SET BOARD_TITLE = #{boardTitle}
	, BOARD_CONTENT = #{boardContent}
	, IS_IMPORTANT = #{isImportant}
WHERE BOARD_NUM = #{boardNum}
AND BOARD_MENU_CODE = #{boardMenuVO.boardMenuCode}

mapper.select:
SELECT FILE_NUM
FROM BOARD_FILE
WHERE BOARD_NUM = #{boardNum}
