mapper.insert:
INSERT INTO DEPT (DEPTNO
				, DENAME
				, LOC)
		VALUES ((SELECT TO_NUMBER(DECODE(#{loc}, '서울', '10', '인천', '20', '부산', '30', '여수', '40') || NVL(MAX(TO_NUMBER(SUBSTR(DEPTNO, 3))), 0) + 1) 
				FROM DEPT WHERE LOC = #{loc})
				, #{dename}
				, #{loc}
)

mapper.select:
SELECT COUNT(DENAME)			
FROM DEPT
WHERE LOC =#{loc}
AND DENAME =#{dename}

mapper.update:
UPDATE DEPT
SET IS_USE = DECODE(IS_USE, 'Y','N','N','Y')
WHERE DEPTNO = #{deptno}
