mapper.insert:
INSERT INTO EMP(EMPNO
				, ENAME
				, EPW
				, JOIN_DATE
				, DEPTNO
				, E_JOB
				, E_STATUS)
		VALUES (TO_CHAR(SYSDATE, 'YY') || #{deptno} || 
				LPAD((SELECT COUNT(*) + 1 FROM EMP WHERE DEPTNO = #{deptno}), 2, '0')
				, #{ename}
				, #{epw}
				, #{joinDate}
				, #{deptno}
				, #{eJob}
				, #{eStatus}
				)

mapper.update:
UPDATE EMP
SET ENAME = #{ename},
	E_EMAIL =#{eEmail},
	OFFICE_TEL =#{officeTel},
	PHONE_TEL =#{phoneTel}
WHERE EMPNO = #{empno}

mapper.select:
SELECT EPW
FROM EMP
WHERE EMPNO =#{empno}

mapper.update:
UPDATE EMP
SET EPW =#{epw}
WHERE EMPNO =#{empno}

mapper.select:
SELECT E_EMAIL
FROM EMP
WHERE EMPNO =#{empno}
AND ENAME =#{ename}

mapper.update:
UPDATE EMP
SET
EPW = #{epw}
WHERE EMPNO =#{empno}
