mapper.insert:
INSERT INTO EMP(EMPNO
				, ENAME
				, EPW
				, JOIN_DATE
				, DEPTNO
				, E_JOB
				, E_STATUS)
		VALUES (TO_CHAR(SYSDATE, 'YY') || #{deptno} || 
				LPAD((SELECT COUNT(*) + 1 FROM EMP WHERE DEPTNO = #{deptno}), 2, '0')
				, #{ename}
				, #{epw}
				, #{joinDate}
				, #{deptno}
				, #{eJob}
				, #{eStatus}
				)

INSTER INTO E_IMG(
		IMG_CODE
		, ORIGIN_FILE_NAME
		, ATTACHED_FILE_NAME
		, EMPNO
		)
	VALUES(
		(SELECT 'IMG_'||LPAD(NVL(MAX(TO_NUMBER(SUMBSTR(IMG_CODE,5))), 0)+1 ,3,'0')FROM E_IMG)
		, #{originFileName}
		, #{attachedFileName}
		, #{empno}
	)
