mapper.select:
SELECT COUNT(ITEM.ITEM_NO) FROM ITEM, ITEM_CATEGORY CATE WHERE ITEM.CATE_NO = CATE.CATE_NO
AND ITEM_NAME LIKE '%'||#{searchItemVO.searchItemName}||'%'
AND ITEM.CATE_NO = #{searchItemVO.searchCateNo}

mapper.update:
UPDATE ITEM
SET	
	CATE_NO = #{cateNo}
	, ITEM_NAME = #{itemName}
	, ITEM_PRICE = #{itemPrice}
	, ITEM_CNT = #{itemCnt}
	, IS_USE = #{isUse}
	, UPDATE_DATE = SYSDATE
WHERE ITEM_NO = #{itemNo}

mapper.insert:
INSERT INTO ITEM (
    ITEM_NO
    , CATE_NO
    , ITEM_NAME
    , ITEM_PRICE
    , ITEM_CNT
    , IS_USE
)
VALUES(
    (SELECT NVL(MAX(ITEM_NO), 0)+1 FROM ITEM)
    , #{categoryVO.cateNo}
    , #{itemName}
    , #{itemPrice}
    , #{itemCnt}
    , #{isUse}
)
