mapper.insert:
INSERT INTO BOARD_REPLY (
	REPLY_NUM
	, REPLY_WRITER
	, REPLY_CONTENT
	, BOARD_NUM
) VALUES (
	#{replyNum}
	, #{replyWriter}
	, #{replyContent}
	, #{boardNum}
)

mapper.update:
UPDATE BOARD_REPLY
SET REPLY_CONTENT = #{replyContent}
	, REPLY_DATE = SYSDATE
WHERE REPLY_NUM = #{replyNum}
